parameters.jsCode:
let items = $input.all();
let minItem = items[0];

for (const item of items) {
  if (item.json.id < minItem.json.id) {
    minItem = item;
  }
}

return [minItem];

// Lấy toàn bộ URL ảnh hợp lệ
const validImageUrls = items
  .map(item => item.json?.images)
  .filter(url => typeof url === 'string' && url.startsWith('http'));

// Tạo danh sách dạng chuỗi
const imageListString = validImageUrls
  .map((url, i) => `${i + 1}. ${url}`)
  .join('\n');

// Gắn lại mỗi item cũ với thêm context imageListString
return items.map(item => {
  return {
    json: {
      ...item.json,
      imageListString,
      allValidImages: validImageUrls
    }
  };
});

return items.flatMap(item => {
const urls = item.json["Ảnh"].split('\n');

return urls.map(url => ({
json: {
"Ảnh": url.trim()
}
}));
});

