let globalIndex = 1;

const chapters = $input.all().flatMap((item) => {
  const content = item.json.output || "";

  const [introRaw, ...rest] = content.split(/^##\s+/gm);

  const introItem = {
    json: {
      chapterIndex: 0,
      chapterTitle: "Intro",
      chapterContent: introRaw.trim()
    }
  };

  const chapterItems = rest.map((ch, i) => {
    const parts = ch.split('\n');
    const title = parts.shift()?.trim() || `Ch∆∞∆°ng ${i + 1}`;
    const body = parts.join('\n').trim();

    return {
      json: {
        chapterIndex: globalIndex++,
        chapterTitle: title,
        chapterContent: `## ${title}\n${body}`
      }
    };
  });

  return [introItem, ...chapterItems];
});

return chapters;

