const items = $input.all();

const postHtml = items.map(item => {
  let { chapterTitle, chapterContent, imageUrl } = item.json;

  // N·∫øu l√† ƒëo·∫°n Intro th√¨ kh√¥ng render heading
  const heading = chapterTitle && chapterTitle !== 'Intro'
    ? `<h2>${chapterTitle}</h2>`
    : '';
  const image = imageUrl
    ? `<img src="${imageUrl}" alt="${chapterTitle}" style="width:100%; margin-bottom: 12px;" />`
    : '';

  if (chapterContent) {
    // ‚úÖ Replace **bold**
    chapterContent = chapterContent.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>');

    // ‚úÖ Replace ### Heading 3
    chapterContent = chapterContent.replace(/^### (.*)$/gm, '<h3>$1</h3>');

    // Optional: x·ª≠ l√Ω line breaks ‚Üí n·∫øu content AI xu·ªëng d√≤ng b·∫±ng `\n`
    chapterContent = chapterContent.replace(/\n/g, '<br />');
  }

  return `<section style="margin-bottom: 32px;">${image}${heading}<div>${chapterContent}</div></section>`;
}).join('\n');

return [
  {
    json: {
      postHtml
    }
  }
];

